//对递归理解的程序,虽然很小,但是有很多精华//递归求最大值#include<iostream>using namespace std;int max(int a[], int head, int tail){	static int temp;	if(head != tail)	{		if(a[head] >= a[tail])		{			temp = a[head];			max(a, head, --tail);		}		else		{			temp = a[tail];			max(a, ++head, tail);		}	}	return temp;}int main(){	int a[100];	int m;	cout << "Please input the array's length(m<100): ";	cin >> m;	cout << "Please input " << m << " numbers: ";	for(int i = 0; i < m; i++)		cin >> a[i];	cout << "The max number is " << max(a,0,m-1) << endl;	getchar();getchar();	return 0;}