#include<stdio.h>#include<stdlib.h>int cmp(const int *p1, const int *p2)   //±È½Ïº¯Êý {       return *p2 - *p1;}int main(){	int n,m;	int *arr;	while(scanf("%d%d",&n,&m)!=EOF)	{        arr = (int *)calloc(n, sizeof(int));		for(int i=0;i<n;i++)			scanf("%d",arr+i);					qsort(arr, n, sizeof(int), cmp);				printf("%d",arr[0]+arr[1]);		for(int i=1;i<m;i++)		{			printf(" %d",arr[i]+arr[i+1]);		}		printf("\n");	}	return 0;}